import Setup from "../components/Setup";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/styles.css';


const requestActivity = async (url) => {
    let response = await fetch(url);
    let activityData = await response.json();
    return activityData
  }

const App = () => {

    const [started, setStarted] = useState(false);
    const [configured, setConfigured] = useState(false);
    const [data, setData] = useState("");

    useEffect (() => {

    },[started])

    const startQuiz = (numOfQuestions, category, difficulty, type) => {
        let baseUrl = "https://opentdb.com/api.php?amount="+numOfQuestions;

        if (category !== "Any Category") {
            baseUrl = baseUrl + "&" + category
        }

        if (difficulty !== "Any Difficulty") {
            baseUrl = baseUrl + "&" + difficulty
        }

        if (type !== "Any Type") {
            baseUrl = baseUrl + "&" + type
        }

        setData(requestActivity(baseUrl))

    } 


    return (
          <div className="main bg-gradient">
            <nav className="navbar navbar-light bg-light">
                <div className="container-fluid">

                <a class="navbar-brand mx-auto" href="#">
                    <img src='/trivia.jpg' alt="" width="200" height="50"/>
                </a>
                </div>
            </nav>
            <div className="container">
            <div className="row">
                    <h1 className="text-center">
                        Let's begin!
                    </h1>
                </div>
                <div className="row">
                    <Setup started={started} start={setStarted} configured={setConfigured} fetch={startQuiz}/>
                </div>
                <div className="row">
                    {

                    }
                </div>

            </div>
        </div>
      )

}



export default App;
