import { useState } from "react";


const Question = props => {


    let style = "btn ";
    let validation = props.validation


    const checkResponse = (answer) => {
        if (answer === props.correct) {
            validation = ("btn-outline-success")
        } else {
            validation = ("btn-outline-danger")
        }

        props.showNext(props.questionNumber + 1)
    }

    return (
        <>
        <h1>{props.question}</h1>
        <div class="btn-group-vertical">
            {
                props.choices.map((each) => 
                <button type="button" class={style+validation} onClick={(e) => checkResponse(e.target.value)}>{each.replace("&quot","\"")}</button>
                )

            }
        </div>
        </>
    )
}

export default Question;
