import {useState, useEffect} from 'react';

let baseURL = "https://opentdb.com/api_config.php";

const requestActivity = async () => {
    console.log(baseURL)
    let response = await fetch(baseURL);
    let activityData = await response.json();
    return activityData
  }

const App = () => {
    useEffect(() => {
        if (status === STATUS.LOADING) {
          let baseURL = "http://www.boredapi.com/api/activity"
          if (type !== "any" || participants !== "any") {
            baseURL += "?"
            if (type !== "any" && participants !== "any") {
              baseURL = baseURL + "type=" + type + "&" + "participants=" + participants
            }
            else if (type !== "any"){
              baseURL = baseURL + "type=" + type
            }
            else if(participants !== "any") {
              baseURL = baseURL + "participants=" + participants
            }
          }
          requestActivity(baseURL).then(data => {
            console.log(data);
            setStatus(STATUS.SUCCESS);
            setActivity(data);
          }).catch(error => {
            setStatus(STATUS.FAIL);
            console.log(error);
          })
        }
      }, [status, type, participants]);
}
