import Configure from "./Configure"
import Question from "./Question"
import QuestionStack from "./QuestionStack"
import Scoreboard from "./Scoreboard"
import Home from "./Home"


const Setup = (props) => {

    if (!props.started) {
        return (<div className="start">
            <h1 className="home">Trivia All Day, All Night</h1>
            <button type="button" class="btn btn-danger btn-lg" onClick={() => props.start(true)}>Start</button>
        </div>)
    } else if (props.scorePresent) {
        return(<Scoreboard score={props.score} total={props.total} configure={props.configure} setScorePresent={props.setScorePresent}/>)

    } else if (!props.configured){
        return (
            <Configure fetch={props.fetch} configured={props.configured}/>
        )
    } else {
        return (
            (props.response === 0)?
            <QuestionStack results={props.results} setScore={props.setScore} setTotal={props.setTotal} setScorePresent={props.setScorePresent}/>
            :""
        )
    }
}

export default Setup;
