import { useState } from "react";


const Question = props => {

    const [validation, setValidation] = useState("btn-outline-info")
    let style = "btn ";
    let change = "btn-outline-info";


    const checkResponse = (answer) => {
        if (answer === props.correct) {
            change = "btn-outline-success"
        } else {
            change = "btn-outline-danger"
        }

        props.showNext(props.questionNumber + 1)
    }

    return (
        <>
        <h1>{props.question}</h1>
        <div class="btn-group-vertical">
            {
                props.choices.map((each) => 
                <button type="button" class={style+change} onClick={(e) => checkResponse(e.target.value)}>{each}</button>
                )

            }
        </div>
        </>
    )
}

export default Question;
