import { useEffect, useState } from "react";


const Question = props => {

    const checkResponse = (answer) => {
        if (answer === props.correct) {
            return true
        }
        return false
    }


    return (
        <>
        <h1>{props.question.replace("&quot","\"")}</h1>
        <div class="btn-group-vertical">
            {
                props.choices.map((each) => 
                <button type="button" class="btn btn-outline-info" value={each.replace("&quot","\"")} 
                onClick={
                    (e) => {
                        checkResponse(e.target.value) ? setValidation("btn-outline-success") :
                        setValidation("btn-outline-danger");
                        props.showNext(props.questionNumber + 1);
                    }
                }>{each.replace("&quot","\"")}</button>
                )
            }
        </div>
        </>
    )
}

export default Question;
