import { useState } from "react";


const Question = props => {

    const [validation, setValidation] = useState(props.default)
    let style = "btn ";


    const checkResponse = (answer) => {
        if (answer === props.correct) {
            setValidation("btn-outline-success")
        } else {
            setValidation("btn-outline-danger")
        }

        props.showNext(props.questionNumber + 1)
    }

    return (
        <>
        <h1>{props.question}</h1>
        <div class="btn-group-vertical">
            {
                props.choices.map((each) => 
                <button type="button" class={style+validation} onClick={(e) => checkResponse(e.target.value)}>{each}</button>
                )

            }
        </div>
        </>
    )
}

export default Question;
