const Scoreboard = props => {
    return(
        <div className="simple">

                <h1 class="card-header trivia text-dark bg-warning">Score</h1>

                <h1 className="card-body trivia text-dark bg-warning">{props.score}/{props.total}</h1>


            <button type="button" class="btn btn-danger btn-lg quiz-button" onClick={() => {
                props.setScorePresent(false)
                props.configure(false)
            }
            }>Play again ?</button>

        </div>
    )
}

export default Scoreboard;
