import { useEffect, useState } from "react";

const replaceCharacters = (line) => {

    line = line.replace("&nbsp;"," ")
    line = line.replace("&#160;"," ")
    line = line.replace("&lt;","<")
    line = line.replace("&#60;","<")
    line = line.replace("&gt;",">")
    line = line.replace("&#62;",">")
    line = line.replace("&amp;","&")
    line = line.replace("&#38;","")
    line = line.replace("","")
    line = line.replace("","")
    line = line.replace("","")
    line = line.replace("","")
} 

const Question = props => {

    const checkResponse = (answer) => {
        if (answer === props.correct) {
            return true
        }
        return false
    }


    return (
        <>
        <h1>{props.question.replace("&quot","\"")}</h1>
        <div class="btn-group-vertical">
            {
                props.choices.map((each) => 
                <button type="button" class="btn btn-outline-info" value={each.replace("&quot","\"")} 
                onClick={
                    (e) => {
                        props.showNext(props.questionNumber + 1);
                    }
                }>{each.replace("&quot","\"")}</button>
                )
            }
        </div>
        </>
    )
}

export default Question;
