const Scoreboard = props => {
    return(
        <div className="simple">
            <div className="card trivia text-dark bg-warning">
                <div class="card-header trivia text-dark bg-warning">Score</div>
                <div className="card-body">
                <h1 className="trivia card-title text-dark bg-warning">{props.score}/{props.total}</h1>
                <h2 className="trivia card-text">{props.score}/{props.total}</h2>
                </div>
            </div>
            <button type="button" class="btn btn-danger btn-lg quiz-button" onClick={() => {
                props.setScorePresent(false)
                props.configure(false)
            }
            }>Play again ?</button>

        </div>
    )
}

export default Scoreboard;
