import { useEffect, useState } from "react";
import * as shuffle from 'shuffle-array';
import Question from "./Question";

const QuestionStack = props => {

    const [currentQuestion, setCurrentQuestion] = useState(props.results[0]);
    const [questionNumber, setQuestionNumber] = useState(0);
    const [choices, setChoices] = useState([]);
    const [validation, setValidation] = useState("btn-outline-info")

    useEffect(() => {
        console.log("question: ",props.results[questionNumber])
        setCurrentQuestion(props.results[questionNumber])
        setValidation()

    },[questionNumber])

    useEffect(() => {
        let options = []
        options.push(...currentQuestion["incorrect_answers"], currentQuestion["correct_answer"])
        console.log("Choices: ",options)
        setChoices(options)
    },[currentQuestion])


    return (
        <Question question={currentQuestion["question"]} choices={choices} correct={currentQuestion["correct_answer"]} questionNumber={questionNumber} showNext={setQuestionNumber} setValidation={setValidation} validation={validation}/>
    )


}

export default QuestionStack;
