import { useEffect, useState } from "react";


const Question = props => {

    const [validation, setValidation] = useState("btn-outline-info")
    const [questionNumber, setQuestionNumber] = useState(props.questionNumber)

    let style = "btn ";


    const checkResponse = (answer) => {
        if (answer === props.correct) {
            setValidation("btn-outline-success")
        } else {
            setValidation("btn-outline-danger")
        }

        props.showNext(props.questionNumber + 1)
    }

    useEffect(() => {
        setQuestionNumber(questionNumber + 1)
    },[validation])


    useEffect(() => {
        setValidation("btn-outline-info")
    },[questionNumber])

    return (
        <>
        <h1>{props.question.replace("&quot","\"")}</h1>
        <div class="btn-group-vertical">
            {
                props.choices.map((each) => 
                <button type="button" class={style+validation} value={each.replace("&quot","\"")} 
                onClick={(e) => checkResponse(e.target.value)}>{each.replace("&quot","\"")}</button>
                )
            }
        </div>
        </>
    )
}

export default Question;
