import { useEffect, useState } from "react";

const replaceCharacters = (line) => {

    line = line.replaceAll("&quot;","\"")
    line = line.replaceAll("&#34;","\"")
    line = line.replaceAll("&apos;","\'")
    line = line.replaceAll("&#39;","\'")
    line = line.replaceAll("&#039;","\'")
    line = line.replaceAll("&nbsp;"," ")
    line = line.replaceAll("&#160;"," ")
    line = line.replaceAll("&lt;","<")
    line = line.replaceAll("&#60;","<")
    line = line.replaceAll("&gt;",">")
    line = line.replaceAll("&#62;",">")
    line = line.replaceAll("&amp;","&")
    line = line.replaceAll("&#38;","&")

    return line
} 

const Question = props => {

    const state = "btn option "
    const [validation, setValidation] = useState("btn-outline-info")

    const checkResponse = (answer) => {
        if (answer === props.correct) {
            //setValidation("btn-outline-success")
            return true
        }
        //setValidation("btn-outline-danger")
        return false
    }


    return (
        <>
        <h1 className="question">{replaceCharacters(props.question)}</h1>
        <div class="btn-group-vertical">
            {
                props.choices.map((each) => 
                <Choice choice={each} questionNumber={props.questionNumber}/>
                )
            }
        </div>
        </>
    )
}

export default Question;
