import {useState, useEffect} from 'react';
import Setup from "../components/Setup";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/styles.css';
import * as shuffle from 'shuffle-array';


const requestActivity = async (url) => {
    let response = await fetch(url);
    let activityData = await response.json();
    return activityData
  }

const App = () => {

    const [started, setStarted] = useState(false);
    const [configured, setConfigured] = useState(false);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showNext, setShowNext] = useState(false);

    let data;
    let results;
    let question;
    let choices;
    const baseURL = "https://opentdb.com/api.php?amount="


    const startQuiz = (numOfQuestions, category, difficulty, type) => {
        console.log("qs: ",numOfQuestions)
        console.log("category: ",category)
        console.log("difficulty: ",difficulty)
        console.log("type: ",type)
        let url = baseURL+numOfQuestions;

        if (category !== "Any Category") {
            url = url + "&category=" + category
        }

        if (difficulty !== "Any Difficulty") {
            url = url + "&difficulty=" + difficulty.trim()
        }

        if (type !== "Any Type") {
            url = url + "&type=" + type.trim()
        }


        requestActivity(url).then(current => {
            console.log("dataaaaa: ",current);
            data = current
            results = data["results"]
        })
        setConfigured(true)
    }


    useEffect(() => {
        if (question.length > 0) {
        let options = []
            options.push(...question["incorrect_answers"],question["correct_answer"])
            shuffle(options)
            setChoices(options)
        }
    },[question])

    useEffect(() => {
        if (question.length > 0) {
        setCurrentQuestion(currentQuestion+1)
        }
    },[question,currentQuestion])


    return (
          <div className="main bg-gradient">
            <nav className="navbar navbar-light bg-light">
                <div className="container-fluid">

                <a class="navbar-brand mx-auto" href="#">
                    <img src='/trivia.jpg' alt="" width="200" height="50"/>
                </a>
                </div>
            </nav>
            <div className="container">
            <div className="row">
                    <h1 className="text-center">
                        Let's begin!
                    </h1>
                </div>
                <div className="row">
                    <Setup started={started} start={setStarted} configured={configured} configure={setConfigured} 
                    fetch={startQuiz} data={data} response={data["response_code"]} question={question}
                    choices={choices} correct={question["correct_answer"]} showNext={setShowNext}/>
                </div>
                <div className="row">

                </div>

            </div>
        </div>
      )

}



export default App;
