const Selection = (props) => {
    return (
        <>
            <div class="input-group mb-3">
                <label class="input-group-text" for="inputGroupSelect01">{props.label}</label>
                <select class="form-select" id="inputGroupSelect01" onChange={(e) => 
                (props.label === "Select Category") ? (props.selectedCategory(e)):
                (props.label === "Select Difficulty") ? (props.selectedDifficulty(e)):
                (props.label === "Select Type")}>
                    <option selected>{props.options[0]}</option>
                    {
                        props.options.map((option,i) =>
                        (i !== 0) ? 
                        <option value={option}>{option}</option>:""
                        )
                    }
                </select>
            </div>
        </>
    )
}

export default Selection;
