import { useEffect, useState } from "react";
import Choice from "./Choice"

const replaceCharacters = (line) => {

    line = line.replaceAll("&quot;","\"")
    line = line.replaceAll("&#34;","\"")
    line = line.replaceAll("&apos;","\'")
    line = line.replaceAll("&#39;","\'")
    line = line.replaceAll("&#039;","\'")
    line = line.replaceAll("&nbsp;"," ")
    line = line.replaceAll("&#160;"," ")
    line = line.replaceAll("&lt;","<")
    line = line.replaceAll("&#60;","<")
    line = line.replaceAll("&gt;",">")
    line = line.replaceAll("&#62;",">")
    line = line.replaceAll("&amp;","&")
    line = line.replaceAll("&#38;","&")

    return line
} 

const Question = props => {

    return (
        <div className="question">

            <div className="card">
                <div className="card-body justify-content-center">
                    {replaceCharacters(props.question)}
                </div>
            </div>
            <div className="btn-group-vertical">
                {
                    props.choices.map((each) => 
                    <Choice choice={replaceCharacters(each)} questionNumber={props.questionNumber} 
                    correct={props.correct} showNext={props.showNext} score={props.score} updateScore={props.updateScore}/>
                    )
                }
            </div>
        </div>
    )
}

export default Question;
